public class Base64Test {
	@Test
	public void base64Test(){
		StringBuffer configFile = new StringBuffer();
		configFile.append("<?xml version='1.0' encoding='UTF-8'?>");
		configFile.append("<argorse>");
		configFile.append("<netTimeout>10000</netTimeout>");
		configFile.append("<modulus></modulus>");
		configFile.append("<publicExponent>65537</publicExponent>");
		configFile.append("</argorse>");
		System.out.println(Base64.byteArrayToBase64(configFile.toString().getBytes()));
		
		
		try {
			PrivateKey privatKey = RSAUtil.getPrivateKey("", "");
			PublicKey publiKey = RSAUtil.getPublicKey("", "65537");
			System.out.println("私钥为："+Base64.byteArrayToBase64(privatKey.getEncoded()));
			System.out.println("公钥为："+Base64.byteArrayToBase64(publiKey.getEncoded()));
		} catch (Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}

}
